"use client"
import { motion } from "framer-motion"
import { ShoppingCart, Tag, Sparkles } from "lucide-react"

// Define the component as a named function
const IntroAnimation = () => {
  return (
    <motion.div
      className="intro-animation"
      initial={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      transition={{ duration: 0.5 }}
    >
      <motion.div className="intro-background">
        {[...Array(20)].map((_, index) => (
          <motion.div
            key={`intro-particle-${index}`}
            className="intro-particle"
            initial={{
              x: Math.random() * window.innerWidth,
              y: Math.random() * window.innerHeight,
              opacity: 0,
            }}
            animate={{
              opacity: [0, 0.8, 0],
              scale: [0, 1, 0],
            }}
            transition={{
              duration: 3,
              delay: Math.random() * 2,
              repeat: Number.POSITIVE_INFINITY,
              repeatDelay: Math.random() * 2,
            }}
          >
            {index % 3 === 0 ? (
              <ShoppingCart size={16} />
            ) : index % 3 === 1 ? (
              <Tag size={16} />
            ) : (
              <Sparkles size={16} />
            )}
          </motion.div>
        ))}
      </motion.div>

      <div className="intro-content">
        <motion.div
          className="logo-container"
          initial={{ scale: 0 }}
          animate={{
            scale: [0, 1.2, 1],
            rotate: [0, 360, 0],
          }}
          transition={{
            duration: 2.5,
            times: [0, 0.6, 1],
            ease: "easeInOut",
          }}
        >
          <motion.div
            className="logo-circle"
            animate={{
              boxShadow: [
                "0 0 0 0 rgba(0, 123, 255, 0)",
                "0 0 0 20px rgba(0, 123, 255, 0.3)",
                "0 0 0 40px rgba(0, 123, 255, 0)",
              ],
            }}
            transition={{
              duration: 2,
              repeat: Number.POSITIVE_INFINITY,
              repeatDelay: 0.5,
            }}
          >
            <ShoppingCart size={40} className="logo-icon" />
          </motion.div>
        </motion.div>

        <motion.h1
          className="intro-title"
          initial={{ opacity: 0, y: 20 }}
          animate={{
            opacity: 1,
            y: 0,
            textShadow: [
              "0 0 10px rgba(0, 123, 255, 0.5)",
              "0 0 20px rgba(0, 123, 255, 0.8)",
              "0 0 10px rgba(0, 123, 255, 0.5)",
            ],
          }}
          transition={{
            opacity: { duration: 1, delay: 1 },
            y: { duration: 1, delay: 1 },
            textShadow: {
              duration: 2,
              repeat: Number.POSITIVE_INFINITY,
              repeatType: "reverse",
            },
          }}
        >
          Buycision
        </motion.h1>

        <motion.p
          className="intro-subtitle"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 1, delay: 1.5 }}
        >
          AI-Powered Shopping Decisions
        </motion.p>
      </div>
    </motion.div>
  )
}

// Make sure to use default export
export default IntroAnimation
