"use client"

import { useState, useEffect } from "react"
import axios from "axios"
import { motion, AnimatePresence } from "framer-motion"
import { ShoppingCart, Tag, Search, Zap, Sparkles, Star, ChevronRight, Cpu, Database, Globe } from "lucide-react"

export default function BuycisionApp() {
  const [query, setQuery] = useState("")
  const [products, setProducts] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [showIntro, setShowIntro] = useState(true)
  const [searchFocused, setSearchFocused] = useState(false)

  useEffect(() => {
    // Auto-hide intro after 4 seconds
    const timer = setTimeout(() => {
      setShowIntro(false)
    }, 4000)

    return () => clearTimeout(timer)
  }, [])

  const searchProducts = () => {
    if (!query) return
    setLoading(true)
    setError("")

    axios
      .get(`http://localhost:5000/api/search-with-sentiment/${query}`)
      .then((response) => {
        console.log("API Response:", response.data)
        setProducts(response.data)
      })
      .catch((error) => {
        console.error("Error fetching products:", error)
        setError("Failed to fetch products. Try again.")
      })
      .finally(() => {
        setLoading(false)
      })
  }

  // For demo purposes, let's add some sample products if none are returned
  const sampleProducts = [
    {
      name: "Quantum Neural Headset",
      price: "$299.99",
      rating: 4.8,
      image: "/placeholder.svg?height=200&width=200",
      url: "#",
      sentiment: "positive",
      description: "Experience neural-linked computing with quantum processing.",
    },
    {
      name: "HoloLens Pro X",
      price: "$1,299.99",
      rating: 4.9,
      image: "/placeholder.svg?height=200&width=200",
      url: "#",
      sentiment: "positive",
      description: "Next-gen augmented reality with neural feedback.",
    },
    {
      name: "NanoFabric SmartJacket",
      price: "$499.99",
      rating: 4.5,
      image: "/placeholder.svg?height=200&width=200",
      url: "#",
      sentiment: "neutral",
      description: "Self-regulating temperature with embedded AI assistant.",
    },
    {
      name: "BioSync Fitness Implant",
      price: "$199.99",
      rating: 4.7,
      image: "/placeholder.svg?height=200&width=200",
      url: "#",
      sentiment: "positive",
      description: "Subcutaneous fitness tracker with real-time health optimization.",
    },
  ]

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 text-white overflow-hidden relative">
      {/* Dynamic background elements */}
      <div className="fixed inset-0 z-0">
        <div className="absolute inset-0 bg-[radial-gradient(ellipse_at_center,_var(--tw-gradient-stops))] from-indigo-900/20 via-slate-900/50 to-slate-950 opacity-70"></div>

        {/* Grid lines */}
        <div className="absolute inset-0 bg-[url('data:image/svg+xml;base64,')] opacity-20"></div>

        {/* Floating elements */}
        <div className="background-animation">
          {[...Array(15)].map((_, index) => (
            <motion.div
              key={`element-${index}`}
              className="absolute opacity-20"
              initial={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                scale: 0.5 + Math.random() * 1.5,
              }}
              animate={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                rotate: Math.random() * 360,
              }}
              transition={{
                duration: 20 + Math.random() * 30,
                repeat: Number.POSITIVE_INFINITY,
                ease: "linear",
                repeatType: "mirror",
              }}
            >
              {index % 5 === 0 ? (
                <ShoppingCart className="text-cyan-300" size={24} />
              ) : index % 5 === 1 ? (
                <Tag className="text-purple-300" size={24} />
              ) : index % 5 === 2 ? (
                <Cpu className="text-blue-300" size={24} />
              ) : index % 5 === 3 ? (
                <Database className="text-emerald-300" size={24} />
              ) : (
                <Globe className="text-pink-300" size={24} />
              )}
            </motion.div>
          ))}

          {/* Light orbs */}
          {[...Array(8)].map((_, index) => (
            <motion.div
              key={`orb-${index}`}
              className="absolute rounded-full bg-gradient-to-r from-indigo-500/30 to-purple-500/30 blur-xl"
              style={{
                width: 100 + Math.random() * 200,
                height: 100 + Math.random() * 200,
              }}
              initial={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                opacity: 0.1 + Math.random() * 0.2,
              }}
              animate={{
                x: Math.random() * window.innerWidth,
                y: Math.random() * window.innerHeight,
                opacity: [0.1, 0.2, 0.1],
              }}
              transition={{
                duration: 30 + Math.random() * 20,
                repeat: Number.POSITIVE_INFINITY,
                ease: "easeInOut",
                repeatType: "mirror",
              }}
            />
          ))}
        </div>
      </div>

      {/* Intro animation */}
      <AnimatePresence>
        {showIntro && (
          <motion.div
            className="fixed inset-0 z-50 flex items-center justify-center bg-black"
            initial={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 0.8 }}
          >
            <motion.div className="text-center">
              <motion.div
                className="inline-block"
                initial={{ scale: 0.2, opacity: 0 }}
                animate={{
                  scale: [0.2, 1.2, 1],
                  opacity: 1,
                }}
                transition={{
                  duration: 1.5,
                  times: [0, 0.8, 1],
                }}
              >
                <Zap className="w-20 h-20 mx-auto text-cyan-400 mb-4" />
              </motion.div>

              <motion.h1
                className="text-5xl font-bold bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 via-purple-500 to-cyan-400"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.8 }}
              >
                BUYCISION
              </motion.h1>

              <motion.div
                className="mt-4 text-xl text-cyan-300"
                initial={{ y: 20, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.8, duration: 0.8 }}
              >
                <span className="inline-flex items-center">
                  AI-Powered Shopping Decisions
                  <Sparkles className="ml-2 w-5 h-5" />
                </span>
              </motion.div>

              <motion.div
                className="mt-8"
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 1.5, duration: 1 }}
              >
                <motion.div
                  className="h-1 w-40 mx-auto bg-gradient-to-r from-cyan-500 to-purple-500 rounded-full"
                  animate={{ width: ["0%", "100%", "40%"] }}
                  transition={{
                    duration: 2,
                    times: [0, 0.8, 1],
                    repeat: Number.POSITIVE_INFINITY,
                    repeatType: "reverse",
                  }}
                />
              </motion.div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Main content */}
      <div className="container mx-auto px-4 py-12 relative z-10 min-h-screen flex flex-col">
        <motion.div
          className="text-center mb-12"
          initial={{ opacity: 0, y: -50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: showIntro ? 4.2 : 0.2 }}
        >
          <motion.h1
            className="text-5xl md:text-6xl font-bold mb-2 bg-clip-text text-transparent bg-gradient-to-r from-cyan-400 via-purple-500 to-cyan-400"
            animate={{
              backgroundPosition: ["0% center", "100% center", "0% center"],
            }}
            transition={{
              duration: 8,
              repeat: Number.POSITIVE_INFINITY,
              ease: "linear",
            }}
          >
            Buycision
          </motion.h1>

          <motion.div
            className="flex items-center justify-center gap-2 text-xl text-cyan-300"
            initial={{ opacity: 0, y: -20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 1, delay: showIntro ? 4.4 : 0.4 }}
          >
            <span>AI-Powered Shopping Decisions</span>
            <motion.div
              animate={{
                rotate: [0, 10, -10, 0],
                scale: [1, 1.1, 1],
              }}
              transition={{
                duration: 2,
                repeat: Number.POSITIVE_INFINITY,
                repeatDelay: 5,
              }}
            >
              <Sparkles className="w-5 h-5" />
            </motion.div>
          </motion.div>
        </motion.div>

        <motion.div
          className={`search-container relative max-w-2xl mx-auto w-full mb-12 ${searchFocused ? "ring-2 ring-cyan-400 ring-opacity-50" : ""}`}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          transition={{ duration: 0.8, delay: showIntro ? 4.5 : 0.5 }}
        >
          <div className="relative flex items-center">
            <input
              type="text"
              placeholder="Search for futuristic products..."
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              onFocus={() => setSearchFocused(true)}
              onBlur={() => setSearchFocused(false)}
              onKeyDown={(e) => e.key === "Enter" && searchProducts()}
              className="w-full py-4 px-6 pr-16 rounded-full bg-slate-800/50 backdrop-blur-sm border border-slate-700 text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-cyan-400/50 transition-all duration-300"
            />
            <motion.button
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              onClick={searchProducts}
              className="absolute right-2 rounded-full p-3 bg-gradient-to-r from-cyan-500 to-purple-500 text-white"
            >
              <Search size={20} />
            </motion.button>
          </div>

          {/* Animated glow effect */}
          <motion.div
            className="absolute inset-0 -z-10 rounded-full bg-gradient-to-r from-cyan-500/20 to-purple-500/20 blur-md"
            animate={{
              opacity: searchFocused ? 1 : 0.5,
            }}
            transition={{ duration: 0.3 }}
          />
        </motion.div>

        {loading && (
          <motion.div
            className="flex justify-center mb-8"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
          >
            <motion.div
              className="flex items-center gap-2 px-6 py-3 rounded-full bg-slate-800/70 backdrop-blur-sm"
              animate={{
                boxShadow: [
                  "0 0 0px rgba(56, 189, 248, 0)",
                  "0 0 20px rgba(56, 189, 248, 0.5)",
                  "0 0 0px rgba(56, 189, 248, 0)",
                ],
              }}
              transition={{ duration: 2, repeat: Number.POSITIVE_INFINITY }}
            >
              <motion.div
                animate={{ rotate: 360 }}
                transition={{ duration: 1.5, repeat: Number.POSITIVE_INFINITY, ease: "linear" }}
              >
                <Cpu size={20} className="text-cyan-400" />
              </motion.div>
              <span className="text-cyan-300">Processing your request...</span>
            </motion.div>
          </motion.div>
        )}

        {error && (
          <motion.div
            className="text-center mb-8 text-red-400 bg-red-900/20 backdrop-blur-sm py-2 px-4 rounded-lg max-w-md mx-auto"
            initial={{ opacity: 0, y: -10 }}
            animate={{ opacity: 1, y: 0 }}
          >
            {error}
          </motion.div>
        )}

        <AnimatePresence>
          {(products.length > 0 || (!loading && query)) && (
            <motion.div
              className="product-grid grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6 mt-8"
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              transition={{ duration: 0.8 }}
            >
              {(products.length > 0 ? products : sampleProducts).map((product, index) => (
                <motion.div
                  key={index}
                  className="product-card relative overflow-hidden rounded-xl bg-slate-800/40 backdrop-blur-md border border-slate-700/50 group"
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.4, delay: index * 0.1 }}
                  whileHover={{ y: -5 }}
                >
                  {/* Sentiment indicator */}
                  {product.sentiment && (
                    <div
                      className={`absolute top-3 right-3 z-10 rounded-full px-2 py-0.5 text-xs font-medium ${
                        product.sentiment === "positive"
                          ? "bg-green-500/20 text-green-300"
                          : product.sentiment === "negative"
                            ? "bg-red-500/20 text-red-300"
                            : "bg-blue-500/20 text-blue-300"
                      }`}
                    >
                      {product.sentiment.charAt(0).toUpperCase() + product.sentiment.slice(1)}
                    </div>
                  )}

                  <div className="relative h-48 overflow-hidden">
                    <motion.div
                      className="absolute inset-0 bg-gradient-to-b from-transparent to-slate-900/80 z-10"
                      initial={{ opacity: 0 }}
                      whileHover={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    />
                    <motion.img
                      src={product.image || "/placeholder.svg?height=200&width=200"}
                      alt={product.name}
                      className="w-full h-full object-cover"
                      whileHover={{ scale: 1.1 }}
                      transition={{ duration: 0.5 }}
                    />
                  </div>

                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-white mb-1 line-clamp-1">{product.name}</h3>

                    {product.description && (
                      <p className="text-slate-300 text-sm mb-3 line-clamp-2">{product.description}</p>
                    )}

                    <div className="flex justify-between items-center mb-3">
                      <p className="text-cyan-400 font-bold">{product.price}</p>
                      <div className="flex items-center">
                        <Star className="w-4 h-4 text-yellow-400 mr-1 fill-yellow-400" />
                        <span className="text-yellow-100">{product.rating}</span>
                      </div>
                    </div>

                    <motion.a
                      href={product.url}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="block w-full py-2 px-4 bg-gradient-to-r from-cyan-500 to-purple-500 rounded-lg text-white font-medium text-center group-hover:shadow-lg group-hover:shadow-purple-500/20 transition-all duration-300"
                      whileHover={{ scale: 1.02 }}
                      whileTap={{ scale: 0.98 }}
                    >
                      <span className="flex items-center justify-center">
                        Buy Now
                        <motion.span
                          animate={{ x: [0, 4, 0] }}
                          transition={{ duration: 1, repeat: Number.POSITIVE_INFINITY, repeatDelay: 1 }}
                        >
                          <ChevronRight className="ml-1 w-4 h-4" />
                        </motion.span>
                      </span>
                    </motion.a>
                  </div>

                  {/* Glow effect on hover */}
                  <motion.div
                    className="absolute inset-0 -z-10 opacity-0 group-hover:opacity-100 transition-opacity duration-300"
                    initial={{ opacity: 0 }}
                    whileHover={{ opacity: 1 }}
                  >
                    <div className="absolute inset-0 bg-gradient-to-r from-cyan-500/10 to-purple-500/10 blur-xl" />
                  </motion.div>
                </motion.div>
              ))}
            </motion.div>
          )}
        </AnimatePresence>

        {!loading && products.length === 0 && query && (
          <motion.div
            className="text-center mt-12"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5 }}
          >
            <p className="text-slate-300 bg-slate-800/40 backdrop-blur-sm py-4 px-6 rounded-lg inline-block">
              No products found. Try a different search term!
            </p>
          </motion.div>
        )}

        {!query && !loading && products.length === 0 && (
          <motion.div
            className="flex-1 flex flex-col items-center justify-center text-center px-4 mt-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: showIntro ? 4.7 : 0.7, duration: 0.8 }}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ delay: showIntro ? 4.9 : 0.9, duration: 0.8 }}
            >
              <div className="relative">
                <Globe className="w-16 h-16 text-slate-600" />
                <motion.div
                  className="absolute inset-0"
                  animate={{
                    rotate: 360,
                  }}
                  transition={{ duration: 20, repeat: Number.POSITIVE_INFINITY, ease: "linear" }}
                >
                  <div className="absolute top-0 left-1/2 -translate-x-1/2 w-1 h-1 bg-cyan-400 rounded-full" />
                  <div className="absolute top-1/4 right-0 w-1 h-1 bg-purple-400 rounded-full" />
                  <div className="absolute bottom-1/4 left-0 w-1 h-1 bg-cyan-400 rounded-full" />
                  <div className="absolute bottom-0 left-1/2 -translate-x-1/2 w-1 h-1 bg-purple-400 rounded-full" />
                </motion.div>
              </div>
            </motion.div>

            <motion.h2
              className="mt-6 text-2xl font-semibold text-white"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: showIntro ? 5.0 : 1.0, duration: 0.8 }}
            >
              Discover the Future of Shopping
            </motion.h2>

            <motion.p
              className="mt-3 max-w-md text-slate-300"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: showIntro ? 5.1 : 1.1, duration: 0.8 }}
            >
              Enter a product name above to get AI-powered insights and make better purchasing decisions.
            </motion.p>

            <motion.div
              className="mt-8 grid grid-cols-1 md:grid-cols-3 gap-4 max-w-3xl w-full"
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: showIntro ? 5.2 : 1.2, duration: 0.8 }}
            >
              {["Smart Devices", "Wearable Tech", "Home Automation"].map((category, index) => (
                <motion.button
                  key={category}
                  className="py-3 px-4 rounded-lg bg-slate-800/50 backdrop-blur-sm border border-slate-700/50 text-slate-300 hover:bg-slate-700/50 hover:text-white transition-colors duration-300"
                  whileHover={{ y: -2, boxShadow: "0 4px 20px rgba(0, 0, 0, 0.3)" }}
                  whileTap={{ y: 0 }}
                  onClick={() => {
                    setQuery(category)
                    searchProducts()
                  }}
                >
                  {category}
                </motion.button>
              ))}
            </motion.div>
          </motion.div>
        )}
      </div>

      {/* Footer */}
      <motion.footer
        className="py-6 text-center text-slate-500 text-sm relative z-10"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ delay: showIntro ? 5.5 : 1.5 }}
      >
        <p>© {new Date().getFullYear()} Buycision • AI-Powered Shopping Decisions</p>
      </motion.footer>
    </div>
  )
}
