import React, { useEffect, useState } from "react";
import { Bar } from "react-chartjs-2";
import { Chart as ChartJS, CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend } from "chart.js";

// Register Chart.js components
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const Admin = () => {
  const [trafficData, setTrafficData] = useState({
    totalSearches: 0,
    totalClicks: 0,
    searchStats: {},
    clickStats: {},
  });

  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");

  useEffect(() => {
    fetchTrafficData();
  }, []);

  const fetchTrafficData = async () => {
    try {
      const response = await fetch("/api/traffic-report");
      const data = await response.json();
      setTrafficData(data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching traffic data:", error);
      setError("Failed to fetch traffic data.");
      setLoading(false);
    }
  };

  if (loading) {
    return <div>Loading traffic data...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  // Prepare data for the charts
  const searchLabels = Object.keys(trafficData.searchStats);
  const searchValues = Object.values(trafficData.searchStats);

  const clickLabels = Object.keys(trafficData.clickStats);
  const clickValues = Object.values(trafficData.clickStats);

  const searchChartData = {
    labels: searchLabels,
    datasets: [
      {
        label: "Searches",
        data: searchValues,
        backgroundColor: "rgba(54, 162, 235, 0.6)",
        borderColor: "rgba(54, 162, 235, 1)",
        borderWidth: 1,
      },
    ],
  };

  const clickChartData = {
    labels: clickLabels,
    datasets: [
      {
        label: "Clicks",
        data: clickValues,
        backgroundColor: "rgba(255, 99, 132, 0.6)",
        borderColor: "rgba(255, 99, 132, 1)",
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="admin-container">
      <h1>Traffic Analytics</h1>
      <div className="stats-summary">
        <p>Total Searches: {trafficData.totalSearches}</p>
        <p>Total Clicks: {trafficData.totalClicks}</p>
      </div>

      <div className="chart-container">
        <h2>Search Statistics</h2>
        <Bar
          data={searchChartData}
          options={{
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: "Searches by Product",
              },
            },
          }}
        />
      </div>

      <div className="chart-container">
        <h2>Click Statistics</h2>
        <Bar
          data={clickChartData}
          options={{
            responsive: true,
            plugins: {
              title: {
                display: true,
                text: "Clicks by Product",
              },
            },
          }}
        />
      </div>
    </div>
  );
};

export default Admin;
