"use client"

import axios from "axios"
import { useState, useEffect } from "react"
import { motion, AnimatePresence } from "framer-motion"
import { ShoppingCart, Tag, Search, Sparkles, TrendingUp, Star, BarChart } from "lucide-react"
import "./styles.css"
import IntroAnimation from "./intro-animation.jsx"

function App() {
  const [query, setQuery] = useState("")
  const [products, setProducts] = useState([])
  const [loading, setLoading] = useState(false)
  const [error, setError] = useState("")
  const [showIntro, setShowIntro] = useState(true)

  useEffect(() => {
    // Hide intro animation after 4.5 seconds
    const timer = setTimeout(() => setShowIntro(false), 4500)
    return () => clearTimeout(timer)
  }, [])

  useEffect(() => {
    // Log a visit when the page loads
    axios.post("http://localhost:5000/api/track-traffic", { event: "visit", timestamp: new Date() })
      .catch((error) => console.error("Traffic logging error:", error))
  }, [])

  const searchProducts = () => {
    if (!query) return
    setLoading(true)
    setError("")

    axios
      .get(`http://localhost:5000/api/search-with-sentiment/${query}`)
      .then((response) => {
        setProducts(response.data)
      })
      .catch((error) => {
        console.error("Error fetching products:", error)
        setError("Failed to fetch products. Try again.")
      })
      .finally(() => setLoading(false))
  }

  const handleProductClick = (product) => {
    axios.post("http://localhost:5000/api/track-traffic", {
      event: "click",
      product: product.name,
      timestamp: new Date(),
    }).catch((error) => console.error("Traffic logging error:", error))
  }

  return (
    <div className="app-container">
      <AnimatePresence>
        {showIntro ? (
          <IntroAnimation key="intro" />
        ) : (
          <motion.div
            key="main-content"
            className="main-content"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            <div className="header-container">
              <motion.h1 className="title" initial={{ opacity: 0, y: -50 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 1, delay: 0.2 }}>
                Buycision
              </motion.h1>
              <motion.button
                className="traffic-button"
                onClick={() => window.location.href = "/admin"}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                <BarChart size={20} /> View Traffic Analytics
              </motion.button>
            </div>

            <motion.div className="search-container" initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} transition={{ duration: 0.8, delay: 0.5 }}>
              <input type="text" placeholder="Search for products..." value={query} onChange={(e) => setQuery(e.target.value)} className="search-input" />
              <motion.button whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }} onClick={searchProducts} className="search-button">
                <Search size={20} />
              </motion.button>
            </motion.div>

            {loading && <p className="loading">Loading products...</p>}
            {error && <p className="error">{error}</p>}

            <motion.div className="product-grid" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8, delay: 0.8 }}>
              {products.length > 0 ? (
                products.map((product, index) => (
                  <motion.div key={index} className="product-card" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4, delay: index * 0.1 }}>
                    <div className="product-image-container">
                      <img src={product.image || "/placeholder.svg"} alt={product.name} className="product-image" />
                    </div>
                    <div className="product-info">
                      <h3 className="product-title">{product.name}</h3>
                      <p className="product-price">{product.price}</p>
                      <p className="product-sentiment">Sentiment: {product.sentimentLabel}</p>
                      <motion.a href={product.url} target="_blank" rel="noopener noreferrer" className="buy-button" whileHover={{ scale: 1.05 }} onClick={() => handleProductClick(product)}>
                        Buy Now
                      </motion.a>
                    </div>
                  </motion.div>
                ))
              ) : (
                <p className="no-products">No products found. Try searching!</p>
              )}
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  )
}

export default App
