import axios from "axios";
import { useState } from "react";
import { motion } from "framer-motion";
import { ShoppingCart, Tag, Search, BarChart } from "lucide-react";
import "./styles.css";

function App() {
  const [query, setQuery] = useState("");
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [trafficData, setTrafficData] = useState([]);
  const [showTrafficModal, setShowTrafficModal] = useState(false);
  const [adminAuth, setAdminAuth] = useState({ username: "", password: "" });
  const [loggedIn, setLoggedIn] = useState(false);

  // 🔍 Search Products
  const searchProducts = () => {
    if (!query) return;
    setLoading(true);
    setError("");

    axios.get(`http://localhost:5000/api/search-with-sentiment/${query}`)
      .then(response => {
        console.log("API Response:", response.data);
        setProducts(response.data);
      })
      .catch(error => {
        console.error("Error fetching products:", error);
        setError("Failed to fetch products. Try again.");
      })
      .finally(() => {
        setLoading(false);
      });
  };

  // 📊 Log Product Clicks
  const logTraffic = (product) => {
    const timestamp = new Date().toISOString();
    axios.post("http://localhost:5000/api/traffic", {
      productName: product.name,
      productUrl: product.url,
      timestamp,
    }).then(() => console.log(`Logged traffic for: ${product.name}`))
    .catch(error => console.error("Traffic logging error:", error));
  };

  // 🛠 Admin Login to View Traffic
  const handleAdminLogin = () => {
    axios.post("http://localhost:5000/api/view-traffic", adminAuth)
      .then(response => {
        setTrafficData(response.data.trafficLogs);
        setLoggedIn(true);
      })
      .catch(() => alert("Invalid credentials"));
  };

  return (
    <div className="container">
      <div className="background-animation">
        {[...Array(10)].map((_, index) => (
          <motion.div key={`cart-${index}`} className="floating-element">
            <ShoppingCart size={24} />
          </motion.div>
        ))}
        {[...Array(10)].map((_, index) => (
          <motion.div key={`tag-${index}`} className="floating-element">
            <Tag size={24} />
          </motion.div>
        ))}
      </div>

      <motion.h1 className="title">Buycision</motion.h1>
      <motion.p className="subtitle">AI-Powered Shopping Decisions</motion.p>

      <div className="search-container">
        <input
          type="text"
          placeholder="Search for products..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          className="search-input"
        />
        <motion.button whileHover={{ scale: 1.05 }} onClick={searchProducts} className="search-button">
          <Search size={20} />
        </motion.button>
      </div>

      {/* 🚦 View Traffic Button */}
      <motion.button
        whileHover={{ scale: 1.05 }}
        onClick={() => setShowTrafficModal(true)}
        className="traffic-button"
      >
        <BarChart size={20} /> View Traffic
      </motion.button>

      {loading && <p className="loading">Loading products...</p>}
      {error && <p className="error">{error}</p>}

      <div className="product-grid">
        {products.length > 0 ? (
          products.map((product, index) => (
            <motion.div key={index} className="product-card">
              <img src={product.image} alt={product.name} className="product-image" />
              <h3 className="product-title">{product.name}</h3>
              <p className="product-price">{product.price}</p>
              <p className="product-rating">Rating: {product.rating} ⭐</p>
              <p className="product-sentiment">Sentiment: {product.sentimentLabel}</p>
              <motion.a
                href={product.url}
                target="_blank"
                rel="noopener noreferrer"
                className="buy-button"
                onClick={() => logTraffic(product)}
                whileHover={{ scale: 1.05 }}
              >
                Buy Now
              </motion.a>
            </motion.div>
          ))
        ) : (
          <p>No products found. Try searching!</p>
        )}
      </div>

      {/* 🛠 Admin Login Modal */}
      {showTrafficModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>Admin Login</h2>
            {loggedIn ? (
              <div>
                <h3>Website Traffic Data</h3>
                <ul>
                  {trafficData.map((entry, index) => (
                    <li key={index}>
                      {entry.productName} - {new Date(entry.timestamp).toLocaleString()}
                    </li>
                  ))}
                </ul>
                <button onClick={() => setShowTrafficModal(false)}>Close</button>
              </div>
            ) : (
              <div>
                <input type="text" placeholder="Username" onChange={(e) => setAdminAuth({ ...adminAuth, username: e.target.value })} />
                <input type="password" placeholder="Password" onChange={(e) => setAdminAuth({ ...adminAuth, password: e.target.value })} />
                <button onClick={handleAdminLogin}>Login</button>
              </div>
            )}
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
