// Add this code at the beginning of your App component's return statement
// This creates the intro animation overlay

function createParticles() {
    const particles = []
    for (let i = 0; i < 30; i++) {
      const tx = (Math.random() - 0.5) * window.innerWidth * 1.5
      const ty = (Math.random() - 0.5) * window.innerHeight * 1.5
      const delay = Math.random() * 2
      const style = {
        left: `${Math.random() * 100}%`,
        top: `${Math.random() * 100}%`,
        "--tx": `${tx}px`,
        "--ty": `${ty}px`,
        animationDelay: `${delay}s`,
      }
      particles.push(<div key={i} className="particle" style={style}></div>)
    }
    return particles
  }
  // Add this at the beginning of your App component's return statement
  ;<div className="intro-overlay">
    <div className="intro-grid"></div>
    <div className="intro-particles">{createParticles()}</div>
    <div className="intro-content">
      <div className="intro-logo">Buycision</div>
      <div className="intro-tagline">AI-Powered Shopping Decisions</div>
    </div>
  </div>
