"use client";

import axios from "axios";
import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { ShoppingCart, Tag, Search, Sparkles, TrendingUp, Star } from "lucide-react";
import "./styles.css";
import { useRouter } from "next/router"; // For navigation to admin page

function App() {
  const [query, setQuery] = useState("");
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");
  const [showIntro, setShowIntro] = useState(true);

  const router = useRouter(); // For navigation

  useEffect(() => {
    // Hide intro animation after 4.5 seconds
    const timer = setTimeout(() => setShowIntro(false), 4500);
    return () => clearTimeout(timer);
  }, []);

  // 🔥 Track Page Visit
  useEffect(() => {
    axios.post("http://localhost:5000/api/track-traffic", { action: "visit" })
      .catch(err => console.error("Traffic tracking failed:", err));
  }, []);

  const searchProducts = () => {
    if (!query) return;
    setLoading(true);
    setError("");

    axios.get(`http://localhost:5000/api/search-with-sentiment/${query}`)
      .then(response => {
        console.log("API Response:", response.data);
        setProducts(response.data);
      })
      .catch(error => {
        console.error("Error fetching products:", error);
        setError("Failed to fetch products. Try again.");
      })
      .finally(() => setLoading(false));
  };

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      searchProducts();
    }
  };

  // 🔥 Track Product Clicks
  const trackTraffic = (product) => {
    axios.post("http://localhost:5000/api/track-traffic", {
      action: "click",
      productName: product?.name || "N/A",
      productUrl: product?.url || "N/A",
    }).catch(err => console.error("Traffic tracking failed:", err));
  };

  return (
    <div className="app-container">
      <AnimatePresence>
        {!showIntro && (
          <motion.div
            key="main-content"
            className="main-content"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.8 }}
          >
            {/* 🔹 Background Animation */}
            <div className="background-animation">
              {[...Array(10)].map((_, index) => (
                <motion.div key={index} className="floating-element cart-icon"
                  initial={{ x: Math.random() * window.innerWidth, y: Math.random() * window.innerHeight }}
                  animate={{ x: Math.random() * window.innerWidth, y: Math.random() * window.innerHeight, rotate: Math.random() * 360 }}
                  transition={{ duration: 20 + Math.random() * 10, repeat: Infinity, ease: "linear" }}>
                  <ShoppingCart size={24} />
                </motion.div>
              ))}
            </div>

            {/* 🔹 Header */}
            <div className="header-container">
              <motion.h1 className="title" initial={{ opacity: 0, y: -50 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 1 }}>
                Buycision
              </motion.h1>
              <motion.p className="subtitle" initial={{ opacity: 0, y: -20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 1 }}>
                AI-Powered Shopping Decisions
              </motion.p>
            </div>

            {/* 🔹 Search Bar */}
            <motion.div className="search-container" initial={{ opacity: 0, scale: 0.8 }} animate={{ opacity: 1, scale: 1 }} transition={{ duration: 0.8 }}>
              <input type="text" placeholder="Search for products..." value={query} onChange={(e) => setQuery(e.target.value)} onKeyPress={handleKeyPress} className="search-input" />
              <motion.button whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }} onClick={searchProducts} className="search-button">
                <Search size={20} />
              </motion.button>
            </motion.div>

            {/* 🔹 View Traffic Button */}
            <motion.button className="view-traffic" onClick={() => router.push("/admin")} whileHover={{ scale: 1.05 }} whileTap={{ scale: 0.95 }}>
              📊 View Traffic Analytics
            </motion.button>

            {/* 🔹 Loading & Error Messages */}
            {loading && <motion.p className="loading" animate={{ opacity: [0.5, 1, 0.5] }} transition={{ repeat: Infinity, duration: 1.5 }}>Searching for the best products...</motion.p>}
            {error && <p className="error">{error}</p>}

            {/* 🔹 Product List */}
            <motion.div className="product-grid" initial={{ opacity: 0 }} animate={{ opacity: 1 }} transition={{ duration: 0.8 }}>
              {products.length > 0 ? products.map((product, index) => (
                <motion.div key={index} className="product-card" initial={{ opacity: 0, y: 20 }} animate={{ opacity: 1, y: 0 }} transition={{ duration: 0.4, delay: index * 0.1 }}
                  whileHover={{ y: -5, boxShadow: "0 15px 30px rgba(0, 123, 255, 0.2)", transition: { duration: 0.2 } }}>
                  <img src={product.image || "/placeholder.svg"} alt={product.name} className="product-image" />
                  <h3 className="product-title">{product.name}</h3>
                  <p className="product-price">Price: {product.price}</p>
                  <p className="product-rating">Rating: {product.rating} ⭐</p>

                  {/* 🔹 Sentiment Analysis */}
                  <p className="product-sentiment">
                    Sentiment: 
                    {product.rating > 4 ? " Positive 😊" : product.rating >= 2.5 ? " Neutral 😐" : " Negative 😡"}
                  </p>

                  {/* 🔹 Buy Button */}
                  <motion.a href={product.url} target="_blank" rel="noopener noreferrer" className="buy-button" whileHover={{ scale: 1.05 }} onClick={() => trackTraffic(product)}>
                    Buy Now
                  </motion.a>
                </motion.div>
              )) : <p className="no-products">No products found. Try searching!</p>}
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

export default App;
